<script>
  /*Contador*/
  document.addEventListener("DOMContentLoaded", () => {
    // Seleciona todos os elementos <span> dentro das .porcents
    const counters = document.querySelectorAll(".statistic .porcents span");
    
    // Configura a função de contador
    const animateCount = (element, start, end, duration) => {
        let startTime = null;

        const step = (currentTime) => {
            if (!startTime) startTime = currentTime;
            const progress = Math.min((currentTime - startTime) / duration, 1);
            element.textContent = Math.floor(progress * (end - start) + start) + "%";
            if (progress < 1) {
                requestAnimationFrame(step);
            }
        };

        requestAnimationFrame(step);
    };

    // Define o observer para ativar o contador ao aparecer na tela
    const observer = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const span = entry.target;
                const endValue = parseInt(span.textContent);
                animateCount(span, 0, endValue, 2000); // Contagem de 0 ao valor final em 2 segundos
                observer.unobserve(span); // Remove o observer depois que a animação iniciar
            }
        });
    }, { threshold: 0.5 }); // Inicia quando 50% do elemento estiver visível

    // Observa cada contador
    counters.forEach(counter => {
        observer.observe(counter);
    });
});

</script>
    
